class Solution:
    # @param A : string
    # @return an integer
    def solve(self, A):
        s = 0
        e = len(A)-1
        # print("Outside")
        # print("Start :",s,"End :",e,"String : ",A)
        def isPalindrom(A,s,e):
            # print("Inside")
            # print("Start :", s, "End :", e, "String : ", A)
            if s > e:
                return 1

            if A[s] == A[e]:
                return isPalindrom(A, s+1, e-1)

            return 0
        return isPalindrom(A,s,e)

s = Solution()
